from dataclasses import dataclass
from typing import Any, Dict, List, Optional

@dataclass
class Version:
    value: Any
    created_txn_id: int
    expired_txn_id: Optional[int] = None

class Transaction:
    def __init__(self, txn_id: int, start_timestamp: int):
        self.txn_id = txn_id
        self.start_timestamp = start_timestamp
        self.status = 'ACTIVE'
        self.read_set: Dict[str, int] = {}
        self.write_set: Dict[str, Any] = {}

class MVCCStore:
    def __init__(self):
        self.store: Dict[str, List[Version]] = {}
        self.active_txns: List[int] = []
        self.next_txn_id: int = 1

    def begin_transaction(self) -> Transaction:
        txn = Transaction(self.next_txn_id, self.next_txn_id)
        self.active_txns.append(txn.txn_id)
        self.next_txn_id += 1
        return txn

    def read(self, txn: Transaction, key: str) -> Optional[Any]:
        if key not in self.store:
            return None
        for version in reversed(self.store[key]):
            if version.created_txn_id < txn.start_timestamp:
                if version.expired_txn_id is None or version.expired_txn_id > txn.start_timestamp:
                    txn.read_set[key] = version.created_txn_id
                    return version.value
        return None

    def write(self, txn: Transaction, key: str, value: Any):
        txn.write_set[key] = value

    def commit(self, txn: Transaction) -> bool:
        if not self._check_write_conflicts(txn):
            return False
        for key, value in txn.write_set.items():
            self._create_new_version(key, value, txn.txn_id)
        txn.status = 'COMMITTED'
        self.active_txns.remove(txn.txn_id)
        return True

    def abort(self, txn: Transaction):
        txn.status = 'ABORTED'
        self.active_txns.remove(txn.txn_id)

    def gc(self):
        active_txn_ids = set(self.active_txns)
        for key in list(self.store.keys()):
            versions_to_remove = []
            for version in self.store[key]:
                if version.created_txn_id not in active_txn_ids:
                    versions_to_remove.append(version)
                    if version.expired_txn_id is None:
                        version.expired_txn_id = self.next_txn_id  # Use next_txn_id as boundary
            for version in versions_to_remove:
                self.store[key].remove(version)

    def _create_new_version(self, key: str, value: Any, txn_id: int):
        if key not in self.store:
            self.store[key] = []
        for version in self.store[key]:
            if version.created_txn_id == txn_id:
                return
            version.expired_txn_id = txn_id  # Expire old version
        self.store[key].append(Version(value, txn_id))

    def _check_write_conflicts(self, txn: Transaction) -> bool:
        for key in txn.write_set:
            if key in self.store:
                for version in self.store[key]:
                    if version.created_txn_id < txn.start_timestamp and (version.expired_txn_id is None or version.expired_txn_id > txn.start_timestamp):
                        return False
        return True

    @property
    def oldest_active_txn(self) -> Optional[int]:
        return min(self.active_txns) if self.active_txns else None
